import './SingleTask.css'
import { useState } from 'react'

function SingleTask({ id, task }) {
    const [checked, setChecked] = useState(false)

    const handleCheck = () => {
        setChecked(!checked)
    }
    return (
        <div className="singletask-container">
            <div className="singletask-checkbox">
                {checked ? (
                    <i className="fa-solid fa-circle" onClick={handleCheck}></i>
                ) : (
                    <i className="fa-solid fa-circle" onClick={handleCheck}></i>
                )}
            </div>
            <div className="singletask-text">
                <p>Do Clean all the racksboards</p>
            </div>
        </div>
    )
}

export default SingleTask
