import { useEffect, useState } from 'react'
import axios from 'axios'

import './OrgNameUpdate.css'

const serverurl = 'http://localhost:8080'

function OrgNameUpdate() {
    const [orgName, setOrgName] = useState('Hello')

    const handleChange = (e) => {
        setOrgName(e.target.value)
    }

    const handleNameUpdate = async (e) => {
        e.preventDefault()
        const headers = {
            'Content-Type': 'application/json',
            'x-access-token': localStorage.getItem('token'),
        }
        try {
            const data = await axios.patch(
                `${serverurl}/org/editdetails`,
                {
                    name: orgName,
                },
                {
                    headers: headers,
                }
            )
            console.log(data)
        } catch (err) {
            console.log(err)
        }
    }

    useEffect(() => {
        console.log(localStorage.getItem('token'))
        const headers = {
            'x-access-token': localStorage.getItem('token'),
        }
        console.log(headers)
        axios
            .get(`${serverurl}/org/orgname`, { headers: headers })
            .then((res) => {
                console.log(res.data)
                setOrgName(res.data.org)
            })
            .catch((err) => {
                console.log(err)
            })
    }, [])
    return (
        <div className="orgnameupdate-container">
            <h1>Update Name of the organisation</h1>
            <p>
                you can update the organisation name, only condition is it has
                to be unique.
            </p>
            <form onSubmit={handleNameUpdate}>
                <input
                    type="text"
                    name="name"
                    value={orgName}
                    onChange={handleChange}
                />
                <button type="submit">Update</button>
            </form>
        </div>
    )
}

export default OrgNameUpdate
