import './OrgInviteUser.css'
import axios from 'axios'
import { useState } from 'react'
const serverurl = 'http://localhost:8080'
function OrgInviteUser() {
    const [email, setEmail] = useState('')

    const handleChange = (e) => {
        setEmail(e.target.value)
    }

    const handleInvite = async (e) => {
        e.preventDefault()
        const headers = {
            'x-access-token': localStorage.getItem('token'),
        }
        try {
            const data = await axios.post(
                `${serverurl}/org/invite`,
                {
                    email: email,
                },
                { headers: headers }
            )
            console.log(data)
        } catch (err) {
            console.log(err)
        }
    }

    return (
        <div className="orginviteuser-container">
            <h1>Invite User</h1>

            <div className="orginviteuser-main">
                <form onSubmit={handleInvite}>
                    <input
                        type="email"
                        name="name"
                        placeholder="Enter the email of the user you want to invite"
                        onChange={handleChange}
                    />
                    <button type="submit">Invite</button>
                </form>
            </div>
        </div>
    )
}

export default OrgInviteUser
