import './ErrorModal.css'

function ErrorModal({ error }) {
    const handleCancel = () => {
        window.location.replace(error.redirect)
    }

    if (error.show) {
        return (
            <div className="error-modal">
                <div
                    className={`error-modal-dialog ${
                        error.type === 'success' ? 'error-green' : 'error-red'
                    }`}
                >
                    <i className="error-close-button" onClick={handleCancel}>
                        &times;
                    </i>
                    <h1 className="error-modal__title">Error</h1>
                    <p className="error-modal__message">{error.message}</p>
                </div>
            </div>
        )
    } else {
        return null
    }
}

export default ErrorModal
