import { useEffect, useState } from 'react'
import axios from 'axios'
import errors from './errors.json'
import './OrgNameUpdate.css'
import ErrorModal from '../ErrorModal/ErrorModal'
const serverurl = 'http://localhost:8080'

function OrgNameUpdate({ name }) {
    console.log(name)
    const [orgName, setOrgName] = useState(name)

    const [error, setError] = useState(errors['default'])
    const handleChange = (e) => {
        setOrgName(e.target.value)
    }

    const handleNameUpdate = async (e) => {
        e.preventDefault()
        const headers = {
            'Content-Type': 'application/json',
            'x-access-token': localStorage.getItem('token'),
        }
        try {
            const data = await axios.patch(
                `${serverurl}/org/editdetails`,
                {
                    name: orgName,
                },
                {
                    headers: headers,
                }
            )
            console.log(data)
        } catch (err) {
            console.log(err)
        }
    }

    useEffect(() => {
        setOrgName(name)
    }, [name])

    return (
        <div className="orgnameupdate-container">
            {error.show && <ErrorModal error={error} />}
            <h1>Update Name of the organisation</h1>
            <p>
                you can update the organisation name, only condition is it has
                to be unique.
            </p>
            <form onSubmit={handleNameUpdate}>
                <input
                    type="text"
                    name="name"
                    value={orgName}
                    onChange={handleChange}
                />
                <button type="submit">Update</button>
            </form>
        </div>
    )
}

export default OrgNameUpdate
