import './SingleTask.css'
const serverurl = 'http://localhost:8080'
function SingleTask({ id, task }) {
    console.log(task.status)

    const headers = {
        'Content-Type': 'application/json',
        'x-access-token': localStorage.getItem('user'),
    }
    const data = {
        taskId: task._id,
        orgId: id,
    }
    const check = () => {
        axios.patch(
            `${serverurl}/task/checktask/${task._id}`,
            {
                headers: headers,
            },
            data
        )
    }
    return (
        <div className="singletask-container">
            <div className="singletask-checkbox">
                {task.status ? (
                    <i className="fa-regular fa-circle" onClick={check}></i>
                ) : (
                    <i className="fa-solid fa-circle" onClick={check}></i>
                )}
            </div>
            <div className="singletask-text">
                <p className={`${!task.status && 'singletask-notcompleted'}`}>
                    {task.task}
                </p>
            </div>
        </div>
    )
}

export default SingleTask
